sp_widget.client_script:
function($scope,igiMenuService) {
	var c = this;
  $scope.userAccounts = "";
	console.log("IGI user accounts client script:");
	$scope.$watch(function(){
		return igiMenuService.userAccounts;
	}, function (newValue, oldValue) {
		if(typeof newValue == 'object' && "responseCode" in newValue && newValue.responseCode == "user_details.search.success")
			$scope.userAccounts = newValue.result[0].accounts;
  });
	$scope.set_borderColor = function (index) {
		if (index % 4 == 0) {
			return { "border-left" : "5px solid #4ecbe4" }
		}else
		if (index % 4 == 1) {
			return { "border-left" : "5px solid #ff704d" }
		}
		else
		if (index % 4 == 2) {
			return { "border-left" : "5px solid #ff944d" }
		}
		else
		if (index % 4 == 3) {
			return { "border-left" : "5px solid #ffb366" }
		}
	} 
	}

