sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    var json = new global.JSON();
	var req = new sn_ws.RESTMessageV2();
	var session = gs.getSession();
	var authToken = session.getClientData('authToken');
	var userName = session.getClientData('username');
	var entitlementId = request.queryParams.entitlementId;
	var endpoint =  encodeURI('https://ibm-igi-mobileqa.persistent.com/api/v1/servicenow/addEntitlement/' + userName + '/' + entitlementId);

	req.setEndpoint(endpoint);
	req.setHttpMethod('post');
	req.setRequestHeader("Accept","application/json");
	req.setRequestHeader("authToken", authToken);

	var res = req.execute();
	var res1 = res.getBody().toString();

	response.setBody(json.decode(res1));
})(request, response);
